from typing import List

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    sum_of_s = 0

    # Preprocess the grid to find the nearest pole for each point
    nearest_pole = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    # First pass: from top to bottom
    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                nearest_pole[i][j] = 0
            elif i > 0:
                nearest_pole[i][j] = min(nearest_pole[i][j], nearest_pole[i - 1][j] + 1)
            if grid[i][j] == 1:
                nearest_pole[i][j] = 0
            elif j > 0:
                nearest_pole[i][j] = min(nearest_pole[i][j], nearest_pole[i][j - 1] + 1)

    # Second pass: from bottom to top
    for i in range(n, -1, -1):
        for j in range(m, -1, -1):
            if i < n:
                nearest_pole[i][j] = min(nearest_pole[i][j], nearest_pole[i + 1][j] + 1)
            if j < m:
                nearest_pole[i][j] = min(nearest_pole[i][j], nearest_pole[i][j + 1] + 1)

    # Calculate the sum of S(x, y) for each point
    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                sum_of_s += 0
            else:
                sum_of_s += nearest_pole[i][j] ** 2

    return sum_of_s

