from typing import List

def calculate_sum_of_squares(n: int, m: int, grid: List[List[int]]) -> int:
    # Function to calculate the sum of squares of distances for each point in the grid

    # Pre-compute the minimum distances to the nearest pole for each point in the grid
    min_distances = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                min_distances[i][j] = 0
            else:
                if i > 0:
                    min_distances[i][j] = min(min_distances[i][j], min_distances[i - 1][j] + 1)
                if j > 0:
                    min_distances[i][j] = min(min_distances[i][j], min_distances[i][j - 1] + 1)

    for i in range(n, -1, -1):
        for j in range(m, -1, -1):
            if i < n:
                min_distances[i][j] = min(min_distances[i][j], min_distances[i + 1][j] + 1)
            if j < m:
                min_distances[i][j] = min(min_distances[i][j], min_distances[i][j + 1] + 1)

    # Calculate the sum of squares of distances
    sum_of_squares = 0
    for i in range(n + 1):
        for j in range(m + 1):
            sum_of_squares += min_distances[i][j] ** 2

    return sum_of_squares

