            elif board[row][col] == 'D' and row < n - 1:
                next_row = row + 1
            elif board[row][col] == 'U' and row > 0:
                next_row = row - 1

            if not visited[next_row][next_col]:
                visited[next_row][next_col] = True
                queue.append((next_row, next_col, steps + 1))

        return max_steps

