from typing import List

def calculate_sum_of_squares(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    # Calculate prefix sum of poles
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = grid[i][j] + prefix_sum[i - 1][j] + prefix_sum[i][j - 1] - prefix_sum[i - 1][j - 1]

    def sum_of_squares(x1, y1, x2, y2):
        total = prefix_sum[x2][y2] - prefix_sum[x1 - 1][y2] - prefix_sum[x2][y1 - 1] + prefix_sum[x1 - 1][y1 - 1]
        return total

    def is_pole(x, y):
        return grid[x][y] == 1

    def distance_squared(x1, y1, x2, y2):
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    def nearest_pole_distance_squared(x, y):
        left, right, top, bottom = y, y, x, x
        while left > 0 and not is_pole(x, left):
            left -= 1
        while right <= m and not is_pole(x, right):
            right += 1
        while top > 0 and not is_pole(top, y):
            top -= 1
        while bottom <= n and not is_pole(bottom, y):
            bottom += 1

        min_distance_squared = float('inf')
        for i in range(top, bottom + 1):
            for j in range(left, right + 1):
                if is_pole(i, j):
                    min_distance_squared = min(min_distance_squared, distance_squared(x, y, i, j))
        return min_distance_squared

    total_sum = 0
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            total_sum += nearest_pole_distance_squared(i, j)

    return total_sum

