def calculate_sum_of_distances(n: int, m: int, grid: List[List[int]]) -> int:
    # Precompute the prefix sums for the distances to the nearest pole
    prefix_sums = [[0] * (m + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if grid[i][j] == 1:
                prefix_sums[i][j] = (i - 1) ** 2 + (j - 1) ** 2
            else:
                prefix_sums[i][j] = min(prefix_sums[i - 1][j], prefix_sums[i][j - 1]) + (i - 1) ** 2 + (j - 1) ** 2

    # Calculate the sum of all S(x, y) for each point in the grid
    total_sum = 0
    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                total_sum += (i - 0) ** 2 + (j - 0) ** 2
            else:
                total_sum += min(prefix_sums[i][j], prefix_sums[n - i][j], prefix_sums[i][m - j], prefix_sums[n - i][m - j])

    return total_sum

