from typing import List, Tuple
import sys

# Assuming a tree is represented as an adjacency list
class TreeNode:
    def __init__(self, room_id: int, illusion_rate: int):
        self.room_id = room_id
        self.illusion_rate = illusion_rate
        self.children: List['TreeNode'] = []

    def add_child(self, child: 'TreeNode'):
        self.children.append(child)

class Tree:
    def __init__(self, n: int):
        self.nodes: List[TreeNode] = [TreeNode(i + 1, 0) for i in range(n)]

    def add_edge(self, u: int, v: int):
        self.nodes[u - 1].add_child(self.nodes[v - 1])
        self.nodes[v - 1].add_child(self.nodes[u - 1])

    def update_illusion_rate(self, room_id: int, new_rate: int):
        self.nodes[room_id - 1].illusion_rate = new_rate

    def calculate_minimum_energy(self, start_room: int, end_room: int) -> int:
        def dfs(node: TreeNode, parent: TreeNode, target: int) -> int:
            if node.room_id == target:
                return 0
            min_energy = sys.maxsize
            for child in node.children:
                if child != parent:
                    energy = dfs(child, node, target)
                    if energy != -1:
                        min_energy = min(min_energy, max(abs(node.illusion_rate - child.illusion_rate), abs(node.illusion_rate - child.illusion_rate)))
            return min_energy if min_energy != sys.maxsize else -1

        return dfs(self.nodes[start_room - 1], None, end_room)

def main():
    # Read input
    n, q = map(int, sys.stdin.readline().split())
    illusion_rates = list(map(int, sys.stdin.readline().split()))
    tree = Tree(n)

    # Build the tree
    for _ in range(n - 1):
        u, v = map(int, sys.stdin.readline().split())
        tree.add_edge(u - 1, v - 1)

    # Set initial illusion rates
    for i in range(n):
        tree.nodes[i].illusion_rate = illusion_rates[i]

    # Process queries
    for _ in range(q):
        query = list(map(int, sys.stdin.readline().split()))
        if query[0] == 1:
            room_id, new_rate = query[1], query[2]
            tree.update_illusion_rate(room_id, new_rate)
        elif query[0] == 2:
            start_room, end_room = query[1], query[2]
            result = tree.calculate_minimum_energy(start_room - 1, end_room - 1)
            print(result)

if __name__ == "__main__":
    main()

