from typing import List, Tuple
from collections import deque

def find_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    # Directions mapping
    directions = {
        'L': (-1, 0),
        'R': (1, 0),
        'D': (0, 1),
        'U': (0, -1)
    }

    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m

    max_steps = 0
    start_position = (0, 0)

    # Try all starting positions
    for i in range(n):
        for j in range(m):
            visited = [[False] * m for _ in range(n)]
            queue = deque([(i, j, 0)])  # (row, col, steps)
            visited[i][j] = True

            while queue:
                x, y, steps = queue.popleft()

                if steps > max_steps:
                    max_steps = steps
                    start_position = (i, j)

                for direction, (dx, dy) in directions.items():
                    nx, ny = x + dx, y + dy
                    if is_valid(nx, ny) and not visited[nx][ny]:
                        visited[nx][ny] = True
                        queue.append((nx, ny, steps + 1))

    return start_position[0] + 1, start_position[1] + 1, max_steps

