def calculate_sum_of_s(grid: np.ndarray) -> int:
    n, m = grid.shape
    result = 0

    # Iterate over each point in the grid
    for i in range(n):
        for j in range(m):
            if grid[i, j] == 1:
                result += 0  # S(x, y) for a pole is 0
            else:
                min_dist = float('inf')
                # Find the nearest pole
                for x in range(n):
                    for y in range(m):
                        if grid[x, y] == 1:
                            dist = (i - x)**2 + (j - y)**2
                            if dist < min_dist:
                                min_dist = dist
                result += min_dist
    return result

