from collections import deque

def find_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    def bfs(start_row: int, start_col: int) -> int:
        visited = [[False] * m for _ in range(n)]
        queue = deque([(start_row, start_col, 0)])  # (row, col, steps)
        visited[start_row][start_col] = True
        max_steps = 0

        while queue:
            row, col, steps = queue.popleft()
            max_steps = max(max_steps, steps)
            direction = board[row][col]

            if direction == 'L':
                if col > 0 and not visited[row][col - 1]:
                    visited[row][col - 1] = True
                    queue.append((row, col - 1, steps + 1))
            elif direction == 'R':
                if col < m - 1 and not visited[row][col + 1]:
                    visited[row][col + 1] = True
                    queue.append((row, col + 1, steps + 1))
            elif direction == 'D':
                if row < n - 1 and not visited[row + 1][col]:
                    visited[row + 1][col] = True
                    queue.append((row + 1, col, steps + 1))
            elif direction == 'U':
                if row > 0 and not visited[row - 1][col]:
                    visited[row - 1][col] = True
                    queue.append((row - 1, col, steps + 1))

        return max_steps

    max_steps = 0
    best_start = (0, 0, 0)

    for row in range(n):
        for col in range(m):
            steps = bfs(row, col)
            if steps > max_steps:
                max_steps = steps
                best_start = (row + 1, col + 1, steps)

    return best_start

