from typing import List

def calculate_sum_of_squares(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    total_sum = 0

    # Precompute the nearest pole distance for each point
    nearest_pole_distance = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                nearest_pole_distance[x][y] = 0
            elif x > 0:
                nearest_pole_distance[x][y] = min(nearest_pole_distance[x][y], nearest_pole_distance[x - 1][y] + 1)
            if y > 0:
                nearest_pole_distance[x][y] = min(nearest_pole_distance[x][y], nearest_pole_distance[x][y - 1] + 1)

    for x in range(n + 1):
        for y in range(m + 1):
            if x < n:
                nearest_pole_distance[x][y] = min(nearest_pole_distance[x][y], nearest_pole_distance[x + 1][y] + 1)
            if y < m:
                nearest_pole_distance[x][y] = min(nearest_pole_distance[x][y], nearest_pole_distance[x][y + 1] + 1)

    for x in range(n + 1):
        for y in range(m + 1):
            total_sum += nearest_pole_distance[x][y] ** 2

    return total_sum

