from typing import List

def calculate_sum_of_squares(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1

    # Create a prefix sum array to store the sum of poles up to (i, j)
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    # Populate the prefix sum array
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = grid[i][j] + prefix_sum[i-1][j] + prefix_sum[i][j-1] - prefix_sum[i-1][j-1]

    def sum_of_squares(x1, y1, x2, y2):
        total = prefix_sum[x2][y2] - prefix_sum[x1-1][y2] - prefix_sum[x2][y1-1] + prefix_sum[x1-1][y1-1]
        return total

    total_sum = 0
    for x in range(1, n + 1):
        for y in range(1, m + 1):
            if grid[x][y] == 1:
                total_sum += 0  # S(x, y) is 0 for a pole itself
            else:
                # Calculate the sum of squares for the nearest pole
                min_distance_squared = float('inf')
                for i in range(1, n + 1):
                    for j in range(1, m + 1):
                        if grid[i][j] == 1:
                            distance_squared = (x - i) ** 2 + (y - j) ** 2
                            min_distance_squared = min(min_distance_squared, distance_squared)
                total_sum += min_distance_squared

    return total_sum

