def func(q: int) -> list:
    array = []
    replacements = {}

    for _ in range(q):
        query = input().split()
        if query[0] == '1':
            x = int(query[1])
            array.append(x)
        elif query[0] == '2':
            x = int(query[1])
            y = int(query[2])
            replacements[x] = y  # Store the replacement in a dictionary

    # Perform replacements after processing all queries
    for i, elem in enumerate(array):
        if elem in replacements:
            array[i] = replacements[elem]

    return array

