from typing import List

def process_queries(queries: List[str]) -> List[int]:
    array = []

    for query in queries:
        parts = query.split()
        if parts[0] == '1':
            x = int(parts[1])
            array.append(x)
        elif parts[0] == '2':
            x = int(parts[1])
            y = int(parts[2])
            for i in range(len(array)):
                if array[i] == x:
                    array[i] = y

    return array

# Read input from stdin
q = int(input())
queries = [input() for _ in range(q)]
result = process_queries(queries)
print(' '.join(map(str, result)))

from typing import List

def find_max_f(sequence: List[int]) -> int:
    max_f = 0
    n = len(sequence)

    for i in range(n):
        if sequence[i] == i + 1:
            max_f += 1

    return max_f

