from typing import List

def func(q: int) -> List[int]:
    array = []

    for _ in range(q):
        query = input().split()
        if query[0] == '1':
            x = int(query[1])
            array.append(x)
        elif query[0] == '2':
            x, y = int(query[1]), int(query[2])
            for i in range(len(array)):
                if array[i] == x:
                    array[i] = y

    return array

