from typing import List

def find_max_f(n: int, sequence: List[int]) -> int:
    f_values = [0] * n

    for i in range(n):
        if sequence[i] == i + 1:
            f_values[i] = 1

    max_f = sum(f_values)

    for i in range(n):
        if f_values[i] == 1:
            current_sequence = sequence[:i] + sequence[i+1:]
            current_f_values = [0] * (n - 1)
            for j in range(n - 1):
                if current_sequence[j] == j + 1:
                    current_f_values[j] = 1
            max_f = max(max_f, sum(current_f_values))

    return max_f

