def calculate_sum_of_s(grid: np.ndarray) -> int:
    n, m = grid.shape
    sum_of_s = 0

    # Pre-compute the distances for each pole
    distances = {}
    for i in range(n):
        for j in range(m):
            if grid[i, j] == 1:
                distances[(i, j)] = (i, j)

    # Calculate the sum of S(x, y) for each point
    for x in range(n):
        for y in range(m):
            if grid[x, y] == 1:
                s_value = 0
            else:
                min_distance_squared = float('inf')
                for (i, j), (dx, dy) in distances.items():
                    distance_squared = (x - i) ** 2 + (y - j) ** 2
                    if distance_squared < min_distance_squared:
                        min_distance_squared = distance_squared
                s_value = min_distance_squared
            sum_of_s += s_value

    return sum_of_s

