from typing import List
import sys

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid)
    m = len(grid[0])
    sum_of_s = 0

    # Pre-compute the minimum distance to the nearest pole for each point
    min_distance = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    for i in range(n + 1):
        for j in range(m + 1):
            if grid[i][j] == 1:
                min_distance[i][j] = 0
            elif i > 0:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i - 1][j] + 1)
            if j > 0:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i][j - 1] + 1)

    for i in range(n + 1):
        for j in range(m + 1):
            if i < n:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i + 1][j] + 1)
            if j < m:
                min_distance[i][j] = min(min_distance[i][j], min_distance[i][j + 1] + 1)

    # Calculate the sum of S(x, y)
    for i in range(n + 1):
        for j in range(m + 1):
            sum_of_s += min_distance[i][j] ** 2

    return sum_of_s

