def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1

    def distance_squared(x1, y1, x2, y2):
        return (x2 - x1) ** 2 + (y2 - y1) ** 2

    def nearest_pole_distance(x, y):
        min_distance = sys.maxsize
        for i in range(n + 1):
            for j in range(m + 1):
                if grid[i][j] == 1:
                    min_distance = min(min_distance, distance_squared(x, y, i, j))
        return min_distance

    total_sum = 0
    for x in range(n + 1):
        for y in range(m + 1):
            total_sum += nearest_pole_distance(x, y)

    return total_sum

