def compare_numbers(x1, p1, x2, p2):
    # Compare the number of trailing zeros
    min_p = min(p1, p2)
    p1 -= min_p
    p2 -= min_p

    # If the number of trailing zeros is different, the result is determined by that
    if p1 != p2:
        return '<' if p1 < p2 else '>'

    # If the number of trailing zeros is the same, compare the numbers without the trailing zeros
    if p1 == 0:
        # Both numbers are now without trailing zeros
        if x1 < x2:
            return '<'
        elif x1 > x2:
            return '>'
        else:
            return '='
    else:
        # Both numbers have trailing zeros removed, compare the original numbers
        while p1 > 0:
            x1 *= 10
            p1 -= 1
        while p2 > 0:
            x2 *= 10
            p2 -= 1

        if x1 < x2:
            return '<'
        elif x1 > x2:
            return '>'
        else:
            return '='

