def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    sum_of_s = 0

    # Precompute the minimum distance to a pole for each point (x, y)
    min_distance = [[float('inf')] * (m + 1) for _ in range(n + 1)]

    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                min_distance[x][y] = 0
            else:
                if x > 0:
                    min_distance[x][y] = min(min_distance[x][y], min_distance[x - 1][y] + 1)
                if y > 0:
                    min_distance[x][y] = min(min_distance[x][y], min_distance[x][y - 1] + 1)

    for x in range(n, -1, -1):
        for y in range(m, -1, -1):
            if x < n:
                min_distance[x][y] = min(min_distance[x][y], min_distance[x + 1][y] + 1)
            if y < m:
                min_distance[x][y] = min(min_distance[x][y], min_distance[x][y + 1] + 1)

    # Calculate the sum of all S(x, y)
    for x in range(n + 1):
        for y in range(m + 1):
            sum_of_s += min_distance[x][y] ** 2

    return sum_of_s

