from typing import List

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1

    # Create a prefix sum array to store the number of poles in the grid
    prefix_sum = [[0] * (m + 1) for _ in range(n + 1)]

    # Populate the prefix sum array
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            prefix_sum[i][j] = grid[i][j] + prefix_sum[i-1][j] + prefix_sum[i][j-1] - prefix_sum[i-1][j-1]

    def sum_of_s(x1, y1, x2, y2):
        total_poles = prefix_sum[x2][y2] - prefix_sum[x1-1][y2] - prefix_sum[x2][y1-1] + prefix_sum[x1-1][y1-1]
        if total_poles == 0:
            return 0

        sum_distances = 0
        for x in range(x1, x2 + 1):
            for y in range(y1, y2 + 1):
                if grid[x][y] == 1:
                    sum_distances += (x - x1)**2 + (y - y1)**2

        return sum_distances

    total_sum = 0
    for x in range(1, n + 1):
        for y in range(1, m + 1):
            total_sum += sum_of_s(x, y, x, y)

    return total_sum

