from typing import List

def find_max_f(sequence: List[int]) -> int:
    n = len(sequence)
    max_f = 0

    for i in range(n):
        if sequence[i] == i + 1:
            max_f += 1

    for i in range(n):
        for j in range(i + 1, n):
            if sequence[i] == i + 1 or sequence[j] == j + 1:
                continue
            sequence[i], sequence[j] = sequence[j], sequence[i]
            current_f = 0
            for k in range(n):
                if sequence[k] == k + 1:
                    current_f += 1
            max_f = max(max_f, current_f)
            sequence[i], sequence[j] = sequence[j], sequence[i]

    return max_f

