import sys
from typing import List, Tuple, Dict

# Initialize the tree structure
tree: Dict[int, List[int]] = {}
illusion_rates: List[int] = []

def update_illusion_rate(room_id: int, new_rate: int) -> None:
    illusion_rates[room_id - 1] = new_rate

def calculate_minimum_energy(start_room: int, end_room: int) -> int:
    def dfs(node: int, parent: int, target: int) -> Tuple[int, int]:
        if node == target:
            return 0, 0

        min_energy = sys.maxsize
        steps = 0
        for neighbor in tree[node]:
            if neighbor == parent:
                continue
            energy, step = dfs(neighbor, node, target)
            if energy != -1:
                if min_energy > max(abs(illusion_rates[node - 1] + illusion_rates[neighbor - 1]), abs(illusion_rates[node - 1] - illusion_rates[neighbor - 1])) + energy:
                    min_energy = max(abs(illusion_rates[node - 1] + illusion_rates[neighbor - 1]), abs(illusion_rates[node - 1] - illusion_rates[neighbor - 1])) + energy
                    steps = step + 1

        return min_energy if min_energy != sys.maxsize else -1, steps if min_energy != sys.maxsize else -1

    result, _ = dfs(start_room, -1, end_room)
    return result if result != -1 else -1

