# Build the tree
for _ in range(n - 1):
    s, t = map(int, sys.stdin.readline().split())
    if s not in tree:
        tree[s] = []
    if t not in tree:
        tree[t] = []
    tree[s].append(t)
    tree[t].append(s)

# Process queries
for _ in range(q):
    query = list(map(int, sys.stdin.readline().split()))
    if query[0] == 1:
        update_illusion_rate(query[1], query[2])
    elif query[0] == 2:
        result = calculate_minimum_energy(query[1], query[2])
        print(result)

