import numpy as np

def calculate_sum_of_s(grid: np.ndarray) -> int:
    n, m = grid.shape
    total_sum = 0

    for x in range(n):
        for y in range(m):
            if grid[x, y] == 1:
                s_value = 0
            else:
                min_distance_squared = float('inf')
                for i in range(n):
                    for j in range(m):
                        if grid[i, j] == 1:
                            distance_squared = (x - i) ** 2 + (y - j) ** 2
                            if distance_squared < min_distance_squared:
                                min_distance_squared = distance_squared
                s_value = min_distance_squared
            total_sum += s_value

    return total_sum

