from typing import List

def find_max_f(sequence: List[int]) -> int:
    def is_fixed_point(index: int, value: int) -> bool:
        return index == value - 1

    max_fixed_points = 0
    n = len(sequence)

    for i in range(n):
        if is_fixed_point(i, sequence[i]):
            max_fixed_points += 1

    for i in range(n):
        current_max = 0
        temp_sequence = sequence[:i] + sequence[i+1:]
        for j in range(len(temp_sequence)):
            if is_fixed_point(j, temp_sequence[j]):
                current_max += 1
        max_fixed_points = max(max_fixed_points, current_max)

    return max_fixed_points

# Read input from stdin
n = int(input())
sequence = list(map(int, input().split()))
result = find_max_f(sequence)
print(result)

from typing import List

def calculate_sum_of_distances(n: int, m: int, grid: List[List[int]]) -> int:
    # Function to calculate the distance from a point to the nearest pole
    def distance_to_nearest_pole(x: int, y: int) -> int:
        min_distance_squared = float('inf')
        for i in range(n + 1):
            for j in range(m + 1):
                if grid[i][j] == 1:
                    distance_squared = (x - i) ** 2 + (y - j) ** 2
                    if distance_squared < min_distance_squared:
                        min_distance_squared = distance_squared
        return min_distance_squared

    # Calculate the sum of squares of distances to the nearest pole for each point in the grid
    sum_of_distances = 0
    for x in range(n + 1):
        for y in range(m + 1):
            sum_of_distances += distance_to_nearest_pole(x, y)

    return sum_of_distances

# Read input from stdin
n, m = map(int, input().split())
grid = [list(map(int, input().split())) for _ in range(n + 1)]

