    # Create a list to store the result
    result = a.copy()

    # Create a dictionary to count the occurrences of each number
    count = {}

    # Count the occurrences of each number in the array
    for num in a:
        if num in count:
            count[num] += 1
        else:
            count[num] = 1

    # Find the maximum number of occurrences
    max_occurrences = max(count.values(), default=0)

    # Find the numbers that have the maximum occurrences
    max_numbers = [num for num, occ in count.items() if occ == max_occurrences]

    # Replace zeros with the numbers that have the maximum occurrences
    for i in range(len(result)):
        if result[i] == 0:
            if max_numbers:
                result[i] = max_numbers.pop(0)
            else:
                # If there are no more max numbers, use the next available number
                for num in range(1, n + 1):
                    if num not in count:
                        result[i] = num
                        break

