import math

def find_abc(n: int) -> Tuple[int, int, int]:
    """
    Given a positive integer n, find three distinct positive integers a, b, c such that a + b + c = n
    and gcd(a, b) = c, where gcd(x, y) denotes the greatest common divisor (GCD) of integers x and y.
    """
    for a in range(1, n // 3 + 1):
        for b in range(a + 1, (n - a) // 2 + 1):
            c = n - a - b
            if math.gcd(a, b) == c:
                return (a, b, c)
    return (-1, -1, -1)  # In case no such triplet is found, though the problem guarantees a solution.

