def check_bipartite(n: int, p: List[int]) -> Optional[List[int]]:
    # Create a list to store the bipartite array a
    a = [0] * n

    # Create a list to store the color of each vertex
    color = [-1] * n

    # Create a list to store the adjacency list of the graph
    adj = [[] for _ in range(n)]

    # Build the graph
    for i in range(n):
        for j in range(i + 1, n):
            if p[i] > p[j]:
                adj[i].append(j)
                adj[j].append(i)

    # Function to perform BFS and check for bipartiteness
    def bfs(start: int):
        queue = [start]
        color[start] = 0
        a[start] = p[start] if color[start] == 0 else -p[start]

        while queue:
            u = queue.pop(0)
            for v in adj[u]:
                if color[v] == -1:
                    color[v] = 1 - color[u]
                    a[v] = p[v] if color[v] == 0 else -p[v]
                    queue.append(v)
                elif color[v] == color[u]:
                    return None
        return a

    # Perform BFS for each connected component
    for i in range(n):
        if color[i] == -1:
            result = bfs(i)
            if result is None:
                return None
            else:
                return result

    return a

