def find_last_popped_bubble(bubbles: List[Tuple[int, int]], finalists: List[Tuple[int, int, int]]) -> List[int]:
    def dot_product(v1, v2):
        return v1[0] * v2[0] + v1[1] * v2[1]

    def vector_length(v):
        return (v[0]**2 + v[1]**2)**0.5

    def angle_between(v1, v2):
        return dot_product(v1, v2) / (vector_length(v1) * vector_length(v2))

    def rotate_vector(v, angle):
        cos_theta = cos(angle)
        sin_theta = sin(angle)
        return (v[0] * cos_theta - v[1] * sin_theta, v[0] * sin_theta + v[1] * cos_theta)

    def find_next_bubble(current_bubble, direction):
        for i, bubble in enumerate(bubbles):
            if bubble != current_bubble:
                vector_to_bubble = (bubble[0] - current_bubble[0], bubble[1] - current_bubble[1])
                if angle_between(direction, vector_to_bubble) > 0.9999999999:  # Check if vectors are almost parallel
                    return bubble, vector_to_bubble
        return None, None

    results = []
    for finalist in finalists:
        dx, dy, k = finalist
        direction = (dx, dy)
        current_bubble = None
        for _ in range(k):
            if current_bubble is None:
                # Find the closest bubble in the direction of the vector
                closest_bubble, closest_vector = None, None
                for bubble in bubbles:
                    vector_to_bubble = (bubble[0], bubble[1])
                    if closest_bubble is None or angle_between(direction, vector_to_bubble) > angle_between(direction, closest_vector):
