    # Initialize the number of ways to color the tree
    dp = [[[0] * 6 for _ in range(2**(k + 1))] for _ in range(k + 1)]

    # Set the chosen colors
    color_map = {v: i for v, i in chosen_colors}

    # Helper function to check if two colors are neighbors
    def is_neighbor(c1, c2):
        if c1 == 'White' and c2 in ['White', 'Yellow']:
            return False
        if c1 == 'Yellow' and c2 in ['White', 'Yellow']:
            return False
        if c1 == 'Green' and c2 in ['Green', 'Blue']:
            return False
        if c1 == 'Blue' and c2 in ['Green', 'Blue']:
            return False
        if c1 == 'Red' and c2 in ['Red', 'Orange']:
            return False
        if c1 == 'Orange' and c2 in ['Red', 'Orange']:
            return False
        return True

    # Initialize the dp table
    for i in range(1, 2**k):
        if i in color_map:
            for j in range(6):
                if j != color_map[i]:
                    dp[0][i][j] = 0
                else:
                    dp[0][i][j] = 1
        else:
            for j in range(6):
                dp[0][i][j] = 1

    # Fill the dp table
    for i in range(1, k + 1):
        for j in range(1, 2**(k - i)):
            for c1 in range(6):
                for c2 in range(6):
                    if is_neighbor(c1, c2):
                        dp[i][j][c1] += dp[i - 1][2 * j][c2]
                        dp[i][j][c1] %= MOD

    # Sum up all possible colorings for the root node
    result = 0
    for c in range(6):
        result += dp[k][1][c]
        result %= MOD

    return result

