    def gcd(x: int, y: int) -> int:
        while y:
            x, y = y, x % y
        return x

    def is_beautiful_coloring_possible(d: int) -> bool:
        color = [0] * n
        for i in range(n):
            if a[i] % d == 0:
                color[i] = 1 if (a[i] // d) % 2 == 0 else 2
            else:
                color[i] = 2
        for i in range(1, n):
            if color[i] == color[i - 1]:
                return False
        return True

    # Find the GCD of all elements in the array
    current_gcd = a[0]
    for i in range(1, n):
        current_gcd = gcd(current_gcd, a[i])

    # Check if there is a suitable d
    for d in range(1, current_gcd + 1):
        if is_beautiful_coloring_possible(d):
            return d
    return 0

