def check_consistency(s: str) -> str:
    n = len(s)
    # Define the array a with arbitrary values
    a = [0] * n

    # Check for each possible starting position
    for start in range(n):
        consistent = True
        # Initialize the array a based on the starting position
        for i in range(n):
            j = (start + i) % n
            a[j] = i

        # Check the consistency of the array a based on the string s
        for i in range(n):
            j = (start + i) % n
            k = (start + i + 1) % n
            if s[j] == 'E':
                if a[j] == a[k]:
                    continue
                else:
                    consistent = False
                    break
            elif s[j] == 'N':
                if a[j] != a[k]:
                    continue
                else:
                    consistent = False
                    break
        if consistent:
            return "YES"
    return "NO"

