def can_erase_sequence(n: int, sequence: List[int]) -> str:
    for _ in range(n):
        can_erase = False
        for i in range(len(sequence)):
            if sequence[i] % (i + 1) != 0:
                can_erase = True
                break
        if not can_erase:
            return "NO"
        sequence.pop(i)
        # Adjust the index after popping
        if i < len(sequence):
            i -= 1
    return "YES"

