def find_max_triangle_area(w: int, h: int, horizontal_points: List[int], vertical_points: List[int]) -> int:
    # Calculate the maximum possible area of a triangle with exactly two points on the same side of the rectangle
    max_area = 0

    # Points on the bottom side (y = 0)
    bottom_points = horizontal_points
    # Points on the top side (y = h)
    top_points = horizontal_points

    # Points on the left side (x = 0)
    left_points = vertical_points
    # Points on the right side (x = w)
    right_points = vertical_points

    # Calculate the area for each combination of sides
    for i in range(len(bottom_points)):
        for j in range(i + 1, len(bottom_points)):
            area = (h * (bottom_points[j] - bottom_points[i]))
            if area > max_area:
                max_area = area

    for i in range(len(top_points)):
        for j in range(i + 1, len(top_points)):
            area = (h * (top_points[j] - top_points[i]))
            if area > max_area:
                max_area = area

    for i in range(len(left_points)):
        for j in range(i + 1, len(left_points)):
            area = (w * (left_points[j] - left_points[i]))
            if area > max_area:
                max_area = area

    for i in range(len(right_points)):
        for j in range(i + 1, len(right_points)):
            area = (w * (right_points[j] - right_points[i]))
            if area > max_area:
                max_area = area

    return max_area

