def reconstruct_repertoire(n: int, b: List[int]) -> Optional[List[int]]:
    a = [0] * n
    for i in range(n):
        # Initialize the repertoire for the i-th singer
        a[i] = b[i] // (n - (i + 1) % n)
        # Check if the current repertoire is consistent with all towns
        for j in range(n):
            if (j - i) % n + 1 != (j + 1) and b[j] != a[i] * ((j - i) % n + 1):
                return None
    return a

