def find_positions(s: str, x: int) -> Tuple[int, int, int, int]:
    n = len(s)
    prefix_sums = {}
    current_sum = 0

    # Calculate prefix sums and store the positions of the first number in each sum
    for i in range(n - 1):
        current_sum = int(s[i:i+2])
        if current_sum not in prefix_sums:
            prefix_sums[current_sum] = i

    # Check if the required sum x exists in the prefix sums
    for i in range(n - 1):
        current_sum = int(s[i:i+2])
        if x - current_sum in prefix_sums:
            return (i + 1, i + 2, prefix_sums[x - current_sum] + 1, prefix_sums[x - current_sum] + 2)

    return (-1, -1, -1, -1)  # This line should never be reached due to the problem constraints

