def count_problem_combinations(n: int, topics: List[int], difficulties: List[int]) -> int:
    # Create dictionaries to count occurrences of each topic and difficulty
    topic_count = {}
    difficulty_count = {}

    for i in range(n):
        topic_count[topics[i]] = topic_count.get(topics[i], 0) + 1
        difficulty_count[difficulties[i]] = difficulty_count.get(difficulties[i], 0) + 1

    # Initialize the count of valid combinations
    valid_combinations = 0

    # Count combinations where all topics are different
    for topic in topic_count:
        if topic_count[topic] >= 3:
            valid_combinations += topic_count[topic] * (topic_count[topic] - 1) * (topic_count[topic] - 2) // 6

    # Count combinations where all difficulties are different
    for difficulty in difficulty_count:
        if difficulty_count[difficulty] >= 3:
            valid_combinations += difficulty_count[difficulty] * (difficulty_count[difficulty] - 1) * (difficulty_count[difficulty] - 2) // 6

    # Count combinations where two topics are the same and the third is different
    for topic in topic_count:
        if topic_count[topic] >= 2:
            valid_combinations += topic_count[topic] * (topic_count[topic] - 1) // 2 * (n - topic_count[topic])

    # Count combinations where two difficulties are the same and the third is different
    for difficulty in difficulty_count:
        if difficulty_count[difficulty] >= 2:
            valid_combinations += difficulty_count[difficulty] * (difficulty_count[difficulty] - 1) // 2 * (n - difficulty_count[difficulty])

    return valid_combinations

