def find_forced_lab_cells(grid: List[List[str]], n: int, m: int) -> List[List[str]]:
    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m and grid[x][y] == '.'

    def mark_forced_cells(start_x: int, start_y: int):
        queue = [(start_x, start_y)]
        visited = [[False] * m for _ in range(n)]
        visited[start_x][start_y] = True
        forced_cells = set()

        while queue:
            x, y = queue.pop(0)
            directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
            possible_moves = []

            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if is_valid(nx, ny) and not visited[nx][ny]:
                    possible_moves.append((nx, ny))

            if len(possible_moves) == 1:
                nx, ny = possible_moves[0]
                if grid[nx][ny] == '.':
                    forced_cells.add((nx, ny))
                    queue.append((nx, ny))
                    visited[nx][ny] = True

        for x, y in forced_cells:
            grid[x][y] = '+'

    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'L':
                mark_forced_cells(i, j)

    return grid

