def is_composite(num: int) -> bool:
    if num <= 1:
        return False
    for i in range(2, int(num**0.5) + 1):
        if num % i == 0:
            return True
    return False

def find_largest_composite_subset(arr: List[int]) -> Tuple[int, List[int]]:
    n = len(arr)
    max_subset_size = 0
    best_subset = []

    for mask in range(1, 1 << n):
        subset_sum = 0
        subset_indices = []
        for i in range(n):
            if mask & (1 << i):
                subset_sum += arr[i]
                subset_indices.append(i + 1)  # 1-indexed

        if is_composite(subset_sum):
            if len(subset_indices) > max_subset_size:
                max_subset_size = len(subset_indices)
                best_subset = subset_indices

    return (max_subset_size, best_subset)

