def can_divide_students(n: int, convenience_matrix: List[List[int]]) -> str:
    # Check if the number of students is even
    if n % 2 != 0:
        return "NO"

    # Calculate the total convenience for each day of the week
    total_convenience = [0] * 5
    for row in convenience_matrix:
        for i, convenient in enumerate(row):
            if convenient == 1:
                total_convenience[i] += 1

    # Check if it's possible to divide the students evenly
    for i in range(5):
        for j in range(i + 1, 5):
            # Calculate the number of students that can attend on both days
            both_days = min(total_convenience[i], total_convenience[j])
            # Calculate the number of students that can only attend on one day
            only_one_day = n - both_days * 2
            # Check if the remaining students can be divided evenly
            if only_one_day >= 0 and only_one_day % 2 == 0:
                return "YES"
    return "NO"

