def calculate_min_seconds(test_cases: List[Tuple[int, int, List[Tuple[int, int, int]]]]) -> List[int]:
    def can_detonate_all(mid_seconds: int) -> bool:
        detonated = set()
        for n, k, mines in test_cases:
            for x, y, timer in mines:
                if timer <= mid_seconds:
                    detonated.add((x, y))
                else:
                    for dx in range(-k, k + 1):
                        for dy in range(-k, k + 1):
                            if abs(dx) + abs(dy) <= k:
                                detonated.add((x + dx, y + dy))
        return len(detonated) == sum(n for n, _, _ in test_cases)

    results = []
    for n, k, mines in test_cases:
        low, high = 0, max(timer for _, _, timer in mines)
        while low < high:
            mid = (low + high) // 2
            if can_detonate_all(mid):
                high = mid
            else:
                low = mid + 1
        results.append(low)
    return results

