from typing import List

def find_rooms(n: int, m: int, tiles: List[List[int]]) -> List[int]:
    # Helper function to perform DFS and mark the visited rooms
    def dfs(x: int, y: int):
        if (x, y) in visited:
            return 0
        visited.add((x, y))
        count = 1
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < m and (nx, ny) not in visited:
                if is_connected(tiles[x][y], tiles[nx][ny]):
                    count += dfs(nx, ny)
        return count

    # Helper function to check if two tiles are connected
    def is_connected(tile1: int, tile2: int) -> bool:
        # Define the walls for each tile
        wall_pairs = [
            (1, 4),  # North and South
            (2, 8),  # East and West
        ]
        for pair in wall_pairs:
            if (tile1 & pair[0]) and (tile2 & pair[1]):
                return False
            if (tile2 & pair[0]) and (tile1 & pair[1]):
                return False
        return True

    # Initialize the directions for moving in the grid
    directions = [(-1, 0), (0, 1), (1, 0), (0, -1)]
    visited = set()
    room_sizes = []

    # Iterate through each cell in the grid
    for i in range(n):
        for j in range(m):
            if (i, j) not in visited:
                room_size = dfs(i, j)
                if room_size > 0:
                    room_sizes.append(room_size)

    # Sort the room sizes in descending order
    room_sizes.sort(reverse=True)
    return [size for size in room_sizes]

