        if n % 2 == 1:
            results.append("NO")
            continue

        # Create a bipartite array
        a = []
        for i in range(n):
            if i % 2 == 0:
                a.append(p[i])
            else:
                a.append(-p[i])

        # Check if the array is bipartite
        is_bipartite = True
        for i in range(n):
            for j in range(i + 1, n):
                if i < j and a[i] > a[j]:
                    if (a[i] > 0 and a[j] > 0) or (a[i] < 0 and a[j] < 0):
                        is_bipartite = False
                        break
            if not is_bipartite:
                break

        if is_bipartite:
            results.append("YES")
            results.append(" ".join(map(str, a)))
        else:
            results.append("NO")

    return results

