from typing import List

def find_min_operations(grid: List[List[int]], n: int, m: int) -> int:
    def is_portal(x, y, a, b):
        for i in range(1, a - 1):
            if grid[x + i][y] == 1 or grid[x + i][y + b - 1] == 1:
                return False
        for j in range(1, b - 1):
            if grid[x][y + j] == 1 or grid[x + a - 1][y + j] == 1:
                return False
        return True

    def count_operations(x, y, a, b):
        operations = 0
        for i in range(x, x + a):
            for j in range(y, y + b):
                if grid[i][j] == 1:
                    operations += 1
        return operations

    min_operations = float('inf')
    for x in range(n - 4):
        for y in range(m - 3):
            for a in range(5, n - x + 1):
                for b in range(4, m - y + 1):
                    if is_portal(x, y, a, b):
                        operations = count_operations(x, y, a, b)
                        min_operations = min(min_operations, operations)
    return min_operations if min_operations != float('inf') else 0

