def min_seconds_to_transform(s, t):
    n = len(s)
    dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]

    dp[0][0] = 0

    for i in range(n + 1):
        for j in range(n + 1):
            if i > 0 and j > 0:
                if s[i - 1] == t[j - 1] or s[i - 1] == '?' or t[j - 1] == '?':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1])
                elif s[i - 1] == '1' and t[j - 1] == '1':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1] + 1)
                elif s[i - 1] == '0' and t[j - 1] == '0':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1] + 1)

            if i > 0:
                dp[i][j] = min(dp[i][j], dp[i - 1][j])
            if j > 0:
                dp[i][j] = min(dp[i][j], dp[i][j - 1])

    return dp[n][n] if dp[n][n] != float('inf') else 0

