from typing import List, Tuple

def construct_tree(n: int, restrictions: List[Tuple[int, int, int]]) -> List[Tuple[int, int]]:
    # Initialize the adjacency list to represent the tree
    adj_list = [[] for _ in range(n + 1)]

    # Create a list of edges to be returned
    edges = []

    # Function to add an edge to the tree
    def add_edge(u, v):
        edges.append((u, v))
        adj_list[u].append(v)
        adj_list[v].append(u)

    # Helper function to check if adding an edge would violate a restriction
    def is_valid_edge(u, v, a, b, c):
        # Check if v is on the path from a to c
        def dfs(node, target, parent):
            if node == target:
                return True
            for neighbor in adj_list[node]:
                if neighbor != parent and dfs(neighbor, target, node):
                    return True
            return False

        return not (dfs(a, b, -1) and dfs(b, c, -1))

    # Preprocess restrictions to avoid redundant checks
    restriction_map = {i: [] for i in range(1, n + 1)}
    for a, b, c in restrictions:
        restriction_map[a].append((b, c))
        restriction_map[b].append((a, c))
        restriction_map[c].append((a, b))

    # Construct the tree by adding edges in a way that satisfies all restrictions
    for i in range(1, n + 1):
        for j in range(i + 1, n + 1):
            if all(is_valid_edge(i, j, a, b, c) for a, b in restriction_map[i] for c in restriction_map[j]):
                add_edge(i, j)
                break

    return edges

