import sys

def calculate_sum_of_s(grid: List[List[int]]) -> int:
    n = len(grid) - 1
    m = len(grid[0]) - 1
    sum_of_s = 0

    for x in range(n + 1):
        for y in range(m + 1):
            if grid[x][y] == 1:
                s_value = 0
            else:
                min_distance_squared = sys.maxsize
                for i in range(n + 1):
                    for j in range(m + 1):
                        if grid[i][j] == 1:
                            distance_squared = (x - i) ** 2 + (y - j) ** 2
                            if distance_squared < min_distance_squared:
                                min_distance_squared = distance_squared
                s_value = min_distance_squared
            sum_of_s += s_value

    return sum_of_s

