def find_desert_pairs(N: int, M: int, edges: List[Tuple[int, int]]) -> int:
    # Initialize the graph
    graph = {i: [] for i in range(1, N + 1)}
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    # Function to find the connected components
    def find_components(graph):
        visited = set()
        components = []

        def dfs(node):
            stack = [node]
            component = []
            while stack:
                node = stack.pop()
                if node not in visited:
                    visited.add(node)
                    component.append(node)
                    for neighbor in graph[node]:
                        if neighbor not in visited:
                            stack.append(neighbor)
            return component

        for node in graph:
            if node not in visited:
                components.append(dfs(node))
        return components

    # Function to check if a graph is a cactus
    def is_cactus(graph, edges, component_edges):
        edge_index = {edge: i for i, edge in enumerate(edges)}
        cycle_edges = [False] * len(edges)
        for component in component_edges:
            if len(component) == 1:
                continue
            for edge in component:
                if cycle_edges[edge_index[edge]]:
                    return False
                cycle_edges[edge_index[edge]] = True
        return True

    # Find all possible pairs (L, R)
    components = find_components(graph)
    edge_index = {edge: i for i, edge in enumerate(edges)}
    result = 0
    for L in range(M):
        for R in range(L, M):
            # Create a subgraph with edges from L to R
            subgraph_edges = edges[L:R+1]
            subgraph_graph = {i: [] for i in range(1, N + 1)}
            for u, v in subgraph_edges:
                subgraph_graph[u].append(v)
                subgraph_graph[v].append(u)

            # Check if the subgraph is a desert
            subgraph_components = find_components(subgraph_graph)
            if all(is_cactus(subgraph_graph, subgraph_edges, component) for component in subgraph_components):
                result += 1
    return result

