def sort_array(n: int, arr: list) -> tuple:
    actions = []

    def cyclic_shift(sub_arr, d):
        return sub_arr[-d:] + sub_arr[:-d]

    def find_min_index(sub_arr):
        return sub_arr.index(min(sub_arr))

    def sort_segment(l, r, d):
        nonlocal arr
        segment = arr[l-1:r]
        shifted_segment = cyclic_shift(segment, d)
        arr = arr[:l-1] + shifted_segment + arr[r:]
        actions.append(f"{l} {r} {d}")

    sorted_arr = sorted(arr)
    i = 0
    while arr != sorted_arr:
        min_index = arr.index(min(arr[i:]))
        if min_index != i:
            d = min_index - i
            sort_segment(i + 1, min_index + 1, d)
        i += 1

    return (len(actions), actions)

